mapper.update:
CREATE TEMPORARY TABLE temp_main AS 
SELECT *
FROM ${tableName}
WHERE category = 'メイン'
  AND price <= #{priceLimit} 
ORDER BY RAND()
LIMIT 1;

CREATE TEMPORARY TABLE temp_side_set AS
SELECT *
FROM ${tableName}
WHERE category = 'サイド'
ORDER BY RAND();

mapper.select:
SELECT id, category, name, price
FROM (
    SELECT id, category, name, price, @sum := @sum + price AS cumulative_sum
    FROM (
        SELECT *, @sum := 0 FROM temp_side_set
    ) t
    WHERE @sum + price <= #{priceLimit} - #{mainPrice}
) u
WHERE cumulative_sum <= #{priceLimit} - #{mainPrice}

UNION ALL

SELECT id, category, name, price
FROM temp_main;
